pragma solidity ^0.6.2;

import "./Crowdsale.sol";
import "./KycContract.sol";

contract MyTokenSale is Crowdsale {
 KycContract kyc;
 constructor( uint256 rate, // rate in TKNbits
  address payable wallet,
  IERC20 token,
  KycContract _kyc)

 Crowdsale(rate, wallet, token)
 public
 {
   kyc = _kyc;
 }
 function _preValidatePurchase(address beneficiary, uint256 weiAmmount) internal view override {
  super._preValidatePurchase(beneficiary,weiAmmount);
  require(kyc.kycCompleted(beneficiary),"KYC not completed yet, aborting");
 }
}
